sistema-de-feedback-interativo.pattern:
// HTML
<div class="feedback" id="feedback"></div>
<div class="overlay" id="overlay"></div>

// JavaScript
function mostrarFeedback(mensagem, tipo) {
  const feedback = document.getElementById('feedback');
  const overlay = document.getElementById('overlay');

  feedback.textContent = mensagem;
  feedback.className = `feedback ${tipo}`; // 'sucesso' | 'erro'
  feedback.style.display = 'block';
  overlay.style.display = 'block';

  setTimeout(() => {
    feedback.style.display = 'none';
    overlay.style.display = 'none';
  }, 2000);
}

function tocarSom(frequencia, duracao) {
  const audioContext = new (window.AudioContext || window.webkitAudioContext)();
  const oscillator = audioContext.createOscillator();
  oscillator.frequency.value = frequencia;
  oscillator.connect(audioContext.destination);
  oscillator.start();
  oscillator.stop(audioContext.currentTime + duracao);
}

// CSS
.feedback {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: white;
  padding: 30px 50px;
  border-radius: 20px;
  font-size: 1.5rem;
  font-weight: bold;
  box-shadow: 0 10px 40px rgba(0,0,0,0.3);
  z-index: 1000;
  display: none;
}

.feedback.sucesso { color: #10B981; }
.feedback.erro { color: #EF4444; }
